import classNames from 'classnames/bind';
import styles from './Header.module.css';
import { TouchableOpacity, Text } from 'react-native-web';
import AuthStack from '../../Navigation/AuthStack';
import { NavigationContainer } from '@react-navigation/native';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { NavLink } from 'react-router-dom';
const cx = classNames.bind(styles);

const action = [{ title: 'Login', to: '/login', icon: <FontAwesomeIcon icon={faUser} /> }];

function Header({ navigation }) {
  return (
    <div className={cx('wrapper')}>
      <h2 className={cx('app-name')}>PRINTER</h2>
      <div className={cx('user-space')}>
        <img
          className={cx('user-avatar')}
          src="https://pm1.aminoapps.com/6850/ee336d8649e9c828b0cc03776e493ee38f4b8776v2_00.jpg"
          alt="avatar"
        />
        <div className={cx('pages')}>
          {actions.map((action, index) => (
            <NavLink to={action.to} key={index} className={(nav) => cx('links', { active: nav.isActive })}>
              <span>{action.icon}</span>
              <div className={cx('page-title')}>{action.title}</div>
            </NavLink>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Header;
